window.addEventListener("load",function(){var i=document.querySelectorAll(".fade-in, .slide-in");i.forEach(function(i){var n=i.getBoundingClientRect().top,e=window.innerHeight/1.2;e>n&&i.classList.add("visible")}),window.addEventListener("scroll",function(){i.forEach(function(i){var n=i.getBoundingClientRect().top,e=window.innerHeight/1.2;e>n&&i.classList.add("visible")})})});